<?php

/**
 * Test class for HH_Filter_HHUrl.
 * Generated by PHPUnit on 2010-09-18 at 14:55:25.
 */
class HH_Filter_HHUrlTest extends PHPUnit_Framework_TestCase
{

    /**
     * @var HH_Filter_HHUrl
     */
    protected $object;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp()
    {
        $this->object = new HH_Filter_HHUrl(Bootstrap::$domains);
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown()
    {

    }

    public function testFilter()
    {
        $in = 'http://www.' . Bootstrap::$rootDomain . '/this/that?q=1#this';
        $out = $this->object->filter($in);
        $this->assertEquals($in, $out);

        $in = 'http://myfarm.' . Bootstrap::$rootDomain . '/this/that?q=1#this';
        $out = $this->object->filter($in);
        $this->assertEquals($in, $out);

        $in = 'http://myfarm.somebadguy.com/this/that?q=1#this';
        $out = $this->object->filter($in);
        $this->assertEquals('/this/that?q=1#this', $out);
    }

}