<?php

/**
 * Test class for HH_Validate_UserNameUnique.
 * Generated by PHPUnit on 2010-09-19 at 11:02:26.
 */
class HH_Validate_UserNameUniqueTest extends PHPUnit_Framework_TestCase
{
    /**
     *
     * @var HH_Domain_Farmer
     */
    private $farmer;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    protected function setUp()
    {
        $data = array(
            'id' => 999999,
            'firstName' => 'Foo',
            'lastName' => 'Bar',
            'email' => 'foo@bar.com',
            'userName' => 'testing',
            'password' => 'testing',
            'addedDatetime' => Zend_Date::now()->setTimezone('UTC'),
            'updatedDatetime' => Zend_Date::now()->setTimezone('UTC'),
            'farmId' => null,
            'role' => HH_Domain_Farmer::ROLE_ADMIN
        );
        $this->farmer = new HH_Domain_Farmer($data['id'], $data);
        $this->farmer->delete();
        $this->farmer = new HH_Domain_Farmer($data['id'], $data);
        $this->farmer->insert($data);
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    protected function tearDown()
    {
        $this->farmer->delete();
    }

    /**
     * @todo Implement testIsValid().
     */
    public function testIsValid()
    {
        $validator = new HH_Validate_UserNameUnique(HH_Domain_Farmer::ROLE_ADMIN);
        $this->assertFalse($validator->isValid($this->farmer['userName']));

        $validator = new HH_Validate_UserNameUnique(HH_Domain_Farmer::ROLE_ADMIN, $this->farmer);
        $this->assertTrue($validator->isValid($this->farmer['userName']));
    }

}